public class BestTimeToBuyAndSellStockMemoizationApproach {
    public int maxProfit(int k, int[] prices) {
        int[][][] memo = new int[prices.length][2][k + 1];

        for (int r = 0; r < memo.length; r++) {
            for (int c = 0; c < memo[r].length; c++) {
                Arrays.fill(memo[r][c], -1);
            }
        }

        return maxProfit(0, 0, k, prices, memo);
    }

    private int maxProfit(int i, int state, int k, int[] prices, int[][][] memo) {
        if (i >= prices.length || k == 0) return 0;
        if (memo[i][state][k] != -1) return memo[i][state][k];

        return memo[i][state][k] = (state == 0) ?
            Math.max(maxProfit(i + 1, 1, k, prices, memo) - prices[i],
                     maxProfit(i + 1, state, k, prices, memo)) :
            Math.max(maxProfit(i + 1, 0, k - 1, prices, memo) + prices[i],
                     maxProfit(i + 1, state, k, prices, memo));
    }
}
