```java
class Solution {

    // If we know max profit made on each day i
    // The answer will be max profit made on day N-1

    // On each day, we have 3 choices
    // 1) Buy - if not owned, use one transaction count
    // 2) Sell - if owned
    // 3) Do nothing

    // Let F(i, isOwned, k) be the max profix on day i, owning the stack or not, using k transactions total
    // F(i, isOwned=true, k) = max(F(i-1, isOwned=true, k), F(i-1, isOwned=false, k-1) - prices[i])
    // F(i, isOwned=false, k) = max(F(i-1, isOwned=false, k), F(i-1, isOwned=true, k) + prices[i])

    // F(i, isOwned, 0) = 0
    // F(0, isOwned=true, k >= 1) = -prices[0]
    // F(0, isOwned=false, k) = 0

    public int maxProfit(int K, int[] prices) {
        if (K == 0 || prices.length == 0) return 0;
        if (K > prices.length / 2) {
            return getMax(prices);
        }

        int N = prices.length;
        int[][] owned = new int[N][K + 1];
        int[][] clear = new int[N][K + 1];

        for (int k = 1; k <= K; k++)
            owned[0][k] = -prices[0]; 

        for (int i = 1; i < N; i++) {
            for (int k = 1; k <= K; k++) {
                owned[i][k] = Math.max(owned[i - 1][k], clear[i - 1][k - 1] - prices[i]);
                clear[i][k] = Math.max(clear[i - 1][k], owned[i - 1][k] + prices[i]);
            }
        }

        return clear[N - 1][K];
    }

    private int getMax(int[] prices) {
        int ans = 0;
        for (int i = 1; i < prices.length; i++) {
            int diff = prices[i] - prices[i - 1];
            ans += Math.max(0, diff);
        }
        return ans;
    }
}
