class Solution {
public:

    int prof(vector<int> prices){
        int profit = 0, n = prices.size();
        for(int i = 1; i < n; i++){
            if(prices[i] > prices[i - 1]) profit += (prices[i] - prices[i - 1]);
        }
        return profit;
    }

    int maxProfit(int k, vector<int>& prices) {
        ios_base::sync_with_stdio(false);
        int n = prices.size();

        if(n == 0) return 0;

        if(k >= n/2) return prof(prices);

        int K = k;
        vector<int> dp(k+1);
        vector<int> minn(K+1, prices[0]);

        for(int i = 1; i<n; i++){
            for(int j = 1; j <= K; j++){
                minn[j] = min(prices[i] - dp[j-1], minn[j]);
                dp[j]   = max(dp[j], prices[i] - minn[j]);
            }
        }
        return dp[k];
    }

};
