        int n = prices.size();
        if(n==0) return 0;
        vector<vector<int>> dp(k+1,vector<int>(n,0));  
        for(int i=1;i<=k;i++){
            int maxx = INT_MIN; 
            for(int j=1;j<n;j++){  
                dp[i][j] = dp[i][j-1]; // previous day profit
                maxx = max(maxx,dp[i-1][j-1]-prices[j-1]); // cantains max profit till now in ( i-1 )th transaction
                dp[i][j] = max(dp[i][j],maxx+prices[j]);
            }
        }
        return dp[k][n-1];
    }
};
