            for (int i=1; i<n; ++i){
                if (prices[i]>prices[i-1]) res+=prices[i]-prices[i-1];
            }
            return res;
        }
        vector<vector<vector<int>>> dp(2, vector<vector<int>>(k, vector<int> (n, -1)));
        return recur(prices, 0, n, k-1, 0, dp);


    }
};
