        }

        for(int l=1;l<=k;l++){

            int ma=0,maxi=dp[n-1][l],tmp=prices[n-1]+dp[n][l-1];

            for(int i=n-2;i>=0;i--){
                      ma=max(ma,max(maxi,tmp-prices[i]));

                      maxi=max(maxi,dp[i][l]);

                      tmp=max(tmp,prices[i]+dp[i+1][l-1]);

                      dp[i][l]=ma;
            }

        }

        return dp[0][k];

    }
};
