	// state2 - current day (idx)
	// state3 - number of txn remaining (k)
    int dp[][][];
    public int maxProfit(int k, int[] prices) {
        int n = prices.length;
        dp = new int[2][n][k+1];
		//base case
        if(n<=1 || k==0)
            return 0;
		//initialize array with -1;
        for(int[][] x:dp)
            for(int[] y:x)
                Arrays.fill(y, -1);
        //as we need to start with a buy assuming prev txn was a sell(0).
        return solve(n, 0, k, 0, prices);
    }

    public int solve(int n, int idx, int k, int buy, int[] prices) {
		//base case 
        if(k == 0 || idx == n)
            return 0;
        if(dp[buy][idx][k] != -1) {
            return dp[buy][idx][k];
        }
        int res = -1;
		//if prev transaction was a buy and currently i am holding the shares
        if(buy == 1) {
			// we have 2 cases if prev txn was a buy
			// either sell it and decrease "k" by 1, as 1 txn has taken place
			int sell =  solve(n, idx+1, k-1, 0, prices)+prices[idx];
			//hold the given stock
			int hold = solve(n, idx+1, k, 1, prices);
             res = Math.max(sell, hold);
