            maxProfit = Math.max(maxProfit, prices[i] - min);
            int q = 0;
            if(maxProfit > 0)
                q = maxProfit + findMax(prices,k-1, i+1);
            max = Math.max(q,max);
        }

        mem[k][start] = max;
        return max;
    }
