class Solution {
public:

int solve(vector<int>& prices,int k ,int index,int op,vector<vector<int>>&dp)
{
    if(index>=prices.size())
    return 0;

    if(op==k*2)
    return 0;

    int profit=0;

    if(dp[index][op]!=-1)
    return dp[index][op];

    if(op%2==0)
    // BUY ALLOWED
    {
        profit = max(-prices[index] + solve(prices, k, index + 1,op+1,dp ), 0 + solve(prices, k, index + 1, op,dp));
    }
    else
    {
        profit = max(prices[index] + solve(prices, k, index + 1,op+1,dp ), 0 + solve(prices, k, index + 1, op,dp));

    }
    return dp[index][op]=profit;
}

    int maxProfit(int k, vector<int>& prices) {
        int n= prices.size();

        vector<vector<int>>dp(n+1,vector<int>(2*k,-1));
        return solve(prices,k,0,0,dp);
    }
};
