class Solution {
public:
    int maxProfit(int k, vector<int>& prices) {
        int n = prices.size(); // Get the number of elements in the prices vector
        int transc  = k; // Set the maximum number of transactions to k
        // Initialize a 3D vector dp to store the maximum profit for each day, for each action (buy or sell), and for each transaction count
        vector<vector<vector<int>>> dp(n+1, vector<vector<int>>(2, vector<int>(k+1, 0)));

        // Iterate over the prices vector backwards, starting from the second-to-last day
        for (int i = n-1; i >= 0; i--) {
            // Iterate over the two possible actions: buy or sell
            for (int canBuy = 0; canBuy <= 1; canBuy++) {
                // Iterate over the possible transaction counts
                for (int k = 1; k <= transc; k++) {
                    int maxProfit = 0; // Initialize the maximum profit to 0

                    // If the current action is buying, calculate the maximum profit of buying or not buying
                    if (canBuy) {
                        int buy = -prices[i] + dp[i+1][0][k]; // Calculate the profit of buying today and selling later
                        int notbuy = 0 + dp[i+1][1][k]; // Calculate the profit of not buying today
                        maxProfit = max(buy, notbuy); // Choose the action that results in the maximum profit
                    }
                    // If the current action is selling, calculate the maximum profit of selling or not selling
                    else {
                        int sell = prices[i] + dp[i+1][1][k-1]; // Calculate the profit of selling today and buying later
                        int notsell = 0 + dp[i+1][0][k]; // Calculate the profit of not selling today
                        maxProfit = max(sell, notsell); // Choose the action that results in the maximum profit
                    }

                    dp[i][canBuy][k] = maxProfit; // Store the maximum profit in the dp vector
                }
            }
        }

        return dp[0][1][transc]; // Return the maximum profit on the first day after making k transactions
    }
};

