public class Solution {
    private int[,,] memo;
    private int[] stockPrices;

    private int dp(int currentDay, int remainingTransactions, int isStockPurchased){
        //Base Case check
            //If no remaining transactions left or if no more days left to do any transaction
        if(remainingTransactions == 0 || currentDay == stockPrices.Length)
            return 0;

        //Start traversing through each state and transit until we find maximum profit made in k transactions
        if(memo[currentDay, remainingTransactions, isStockPurchased] == 0){
            //There are two posibilities here
                //Either do nothing and keep total profit as it is
                //Or purchase stock and deduct price[i] from total profit

            //Total profit if no stock is purchased or sold until currentDay
            int doNothing = dp(currentDay + 1, remainingTransactions, isStockPurchased);

            int doSomething;
            //If stock is purchased already
            if(isStockPurchased == 1){
                //Sell stock
                    //Move to next state with currentDay + 1, 
                    //Reduce reminingTransactions count by 1 as we complete a transaction when we sell a stock
                    //Mark isStockPurchased = 0 as we sold the stock and will not have any stock purchased when moved to next state
                doSomething = stockPrices[currentDay] + dp(currentDay + 1, remainingTransactions-1, 0);
            }else{
                //Buy stock
                    //Move to next state with currentDay + 1
                    //Keep remaining transactions same as we will reduce this count when we sellthe stock
                    //Mark isStockPurchased flag to 1 as stock will be purchased when moved to next state
                doSomething = -stockPrices[currentDay] + dp(currentDay + 1, remainingTransactions, 1);
            }

            memo[currentDay, remainingTransactions, isStockPurchased] = Math.Max(doNothing, doSomething);

        }
        return memo[currentDay,remainingTransactions,isStockPurchased];

    }
    public int MaxProfit(int k, int[] prices) {
        //Top Down Implementation

        //State Variables for DP solution:
            //1. Current day i to determine price of stock prices[i]
            //2. Number of remaining trasnaction k
            //3. If stock is already purchased currently (0 = Not Purchased, 1 = Purchased)
        //Initialize memoization table to perform state transition
        this.stockPrices = prices;
        //Initialize memoization table with state variables
        this.memo = new int[prices.Length, k + 1, 2];
        //Start from the first day with k total transactions remaining and stock is yet not purchased
        return dp(0, k, 0);
    }
}
