    private int[] stockPrices;

    private int dp(int currentDay, int remainingTransactions, int isStockPurchased){
        //Base Case check
            //If no remaining transactions left or if no more days left to do any transaction
        if(remainingTransactions == 0 || currentDay == stockPrices.Length)
            return 0;

