                if(buy[(i-1)%2][j]!=INT_MIN)
                    sell[i%2][j] = max(sell[i%2][j],buy[(i-1)%2][j]+prices[i-1]);

                // don't sell this stock in jth transaction
                sell[i%2][j] = max(sell[i%2][j],sell[(i-1)%2][j]);
            }
        }
        int ans = 0;
        for(int j=0;j<=k;j++)
            ans = max(ans,sell[n%2][j]);
        return ans;
    }
};
