class Solution {

    private Integer[][][] dp;

    private int callMe(int k, int[] prices, int d, int buy) {
        if (d == prices.length || k == 0) { // NO TRANSACTIONS LEFT OR NO PRICES LEFT
            return 0;
        }
        if (dp[k][d][buy] != null) {
            return dp[k][d][buy];
        }
        int x1;
        if (buy == 1) {
            x1 = callMe(k, prices, d + 1, 0) - prices[d]; // BUY
        } else {
            x1 = callMe(k - 1, prices, d + 1, 1) + prices[d]; // SELL
        }
        int x2 = callMe(k, prices, d + 1, buy); // SKIP
        int ans = Math.max(x1, x2);
        return dp[k][d][buy] = ans;
    }

    public int maxProfit(int k, int[] prices) {
        int res = 0;
        if (k > prices.length / 2) {
            for (int i = 1; i < prices.length; i++)
                if (prices[i] > prices[i - 1])
                    res += prices[i] - prices[i - 1];
            return res;
        }
        dp = new Integer[k + 1][prices.length + 1][2];
        return callMe(k, prices, 0, 1);
    }
}
