class Solution:
    def maxProfit(self, k: int, price: List[int]) -> int:
        n = len(price)
        if n <= 1 or k == 0:
            return 0
        if k> n/2:
            max_profit = 0
            for i in range(1,n):
                diff = price[i] - price[i-1]
                if diff > 0:
                    max_profit += diff
            return max_profit

        local_dp = [[0]*n for _ in range(k+1)]
        global_dp = [[0]*n for _ in range(k+1)]
        for i in range(1,n):
            diff = price[i] - price[i-1]
            for kk in range(1,k+1):
                local_dp[kk][i] = max(global_dp[kk-1][i-1]+max(diff,0), local_dp[kk][i-1]+diff)
                global_dp[kk][i] = max(global_dp[kk][i-1], local_dp[kk][i])
        return global_dp[-1][-1]
