class Solution {
private:
    int maxProfitN(vector<int>& p) {
        int res = 0;
        for (int i = 1; i < p.size(); ++i) 
            res += max(0, p[i] - p[i - 1]);
        return res;
    }

public:
    int maxProfit(int k, vector<int>& prices) {
        int buy = 0, i = 0, t = 0;
        int T = k, N = prices.size();

        if (T >= N/2)
            return maxProfitN(prices);

        if (N < 2)
            return 0;

        vector<vector<int>> maxp(T+1, vector<int>(N, 0));
        for(t = 1; t <= T; ++t) {
            buy = prices[0];
            for (i = 1; i < N; ++i) {
                maxp[t][i] = max(maxp[t][i-1], prices[i] - buy);
                buy = min(buy, prices[i] - maxp[t-1][i]);
            }
        }
        return maxp[T][N-1];
    }
};
```

