class Solution:
    def maxProfit(self, k: int, prices: List[int]) -> int:
        n=len(prices)
        ahead=[[0]*(k+1) for _ in range(2)]
        curr=[[0]*(k+1) for _ in range(2)]
        for ind in range(n-1,-1,-1):
            for buy in range(2):
                for cap in range(1,k+1):
                    profit=0
                    if buy==0:
                        take=-prices[ind]+ahead[1][cap]
                        not_take=0+ahead[0][cap]
                        profit=max(take,not_take)
                    else:
                        take=prices[ind]+ahead[0][cap-1]
                        not_take=0+ahead[1][cap]
                        profit=max(take,not_take)
                    curr[buy][cap]=profit
            ahead=curr
        return curr[0][k]
