class Solution {
public:
    int dp[100000][2][3];

    int solve(int index, int buy, int k, vector<int>& prices) {
        if (index >= prices.size() || k == 0) {
            return 0;
        }

        if (dp[index][buy][k] != -1) {
            return dp[index][buy][k];
        }

        int profit = 0;

        if (buy == 1) {
            profit = max(-prices[index] + solve(index + 1, 0, k - 1, prices), solve(index + 1, 1, k, prices));
        } else {
            profit = max(prices[index] + solve(index + 1, 1, k, prices), solve(index + 1, 0, k, prices));
        }

        return dp[index][buy][k] = profit;
    }

    int maxProfit(vector<int>& prices) {
        memset(dp, -1, sizeof(dp));
        return solve(0, 1, 2, prices);
    }
};
```

