# Code
```
class Solution {
public:
    vector<int> tree;
    void queryUpdate(int node, int node_low, int node_high, int l,int h){
        if(node_high < l || node_low > h || node_low > node_high){
            return ;
        }
        if(node_low >= l && node_high <= h){
            tree[node] -= 1;
            return;
        }
        int mid = (node_low + node_high) >> 1;
        queryUpdate(2*node,node_low,mid,l,h);
        queryUpdate(2*node  + 1,mid + 1, node_high,l,h);
        tree[node] = tree[2*node] + tree[2*node + 1];
    }
    int querySum(int node, int node_low, int node_high,int l, int h){
        if(node_high < l || node_low > h || node_low > node_high){
            return 0;
        }
        if(node_low >= l && node_high <= h){
            return tree[node];
        }
        int mid = (node_low + node_high) >> 1;
        return querySum(2*node,node_low,mid,l,h) +
        querySum(2*node  + 1,mid + 1, node_high,l,h);
    }
    int countGoodTriplets(vector<int>& arr, int a, int b, int c) {
        int n = arr.size();
        const int N = 1024;
        vector<int> pre(N);
        vector<int> post(N);
        tree.assign(2*N,0);
        pre[arr[0]]++;
        for(int i = 2; i < n; i++) post[arr[i]]++;
        for(int i = 0; i < N; i++){
            tree[N + i] = post[i]; 
        }
        for(int i = N-1; i >= 1; i--){
            tree[i] = tree[2*i] + tree[2*i+ 1];
        }
        int ans = 0;
        for(int middle = 1; middle < n-1; middle++){
            int v = arr[middle];
            int p1 = max(0,v-a);
            int p2 = min(1000,v + a);
            int t1 = max(0,v - b);
            int t2 = min(1000,v + b);
            for(int s = p1; s <= p2; s++){
                if(pre[s] == 0) continue;
                int m1 = max(t1,s-c);
                int m2 = min(t2,s + c);
                if(m2 >= m1){
                    int val = querySum(1,0,N-1,m1,m2);
                    // cout <<val << endl;
                    ans += pre[s]*(val);
                }
            }
            pre[v]++;
            queryUpdate(1,0,N-1,arr[middle + 1],arr[middle + 1]);
        }
        return ans;
    }
};
```

