impl Solution {
    pub fn count_good_triplets(arr: Vec<i32>, a: i32, b: i32, c: i32) -> i32 {
        let mut counter = 0;
        let l = arr.len();

        arr[..l - 2].iter().enumerate().for_each(|(i, &ai)| {
            arr[..l - 1]
                .iter()
                .enumerate()
                .skip(i + 1)
                .filter(|(_, aj)| (ai - **aj).abs() <= a)
                .for_each(|(j, &aj)| {
                    arr[j + 1..l]
                        .iter()
                        .filter(|&&ak| (aj - ak).abs() <= b && (ai - ak).abs() <= c)
                        .for_each(|_| {
                            counter += 1;
                        })
                })
        });
        counter
    }
}
