class Solution:
    def countGoodTriplets(self, arr: List[int], a: int, b: int, c: int) -> int:
        partial_solutions = list()
        count = 0

        for idx, n in enumerate(arr):
            for arri, arrj in partial_solutions:
                arrk = n
                if abs(arrj - arrk) <= b and abs(arri - arrk) <= c:
                    count += 1

            for arri in arr[:idx]:
                arrj = n
                if abs(arri - arrj) <= a:
                    partial_solutions.append((arri, arrj))

        return count
