class Solution {
    public int countGoodTriplets(int[] arr, int a, int b, int c) {

        int count = 0 ; 
//we make an int count so that whenever we find a good triplet it can be incremented
//and we will return it's value in the end ;

//we simply used nested for loops to find good triplets as the length of the 
//given array is <=100 ;
        for(int i = 0 ; i < arr.length-2 ; i++){
            for(int j = i+1 ; j < arr.length-1 ; j++){
                for(int k = j+1 ; k < arr.length ; k++){
                    boolean x = good(a,b,c,arr[i],arr[j],arr[k]);
                    if(x)count++;
                }
            }
        }
        return count ;
    }
    //this function checks them for a good triplet and return true if they are ;
    public boolean good(int a , int b , int c , int i , int j , int k){

        int first = i-j ; first = Math.abs(first);
        int second = j-k ; second = Math.abs(second);
        int third = k-i ; third = Math.abs(third);
        if(first <= a && second <= b && third <= c) return true;

        return false ;
    }
}
