class Solution:
    def countGoodTriplets(self, arr: List[int], a: int, b: int, c: int) -> int:

        diff_pairs = []
        for i in range(3):
            diff_pairs.append(set())

        count = 0

        for i in range(len(arr)):
            for j in range(i + 1,len(arr)):
                if abs(arr[i] - arr[j]) <= a:
                    diff_pairs[0].add((i,j))
                if abs(arr[i] - arr[j]) <= b:
                    diff_pairs[1].add((i,j))
                if abs(arr[i] - arr[j]) <= c:
                    diff_pairs[2].add((i,j))

        for a_diff in diff_pairs[0]:
            for b_diff in diff_pairs[1]:
                if a_diff[1] == b_diff[0] and (a_diff[0], b_diff[1]) in diff_pairs[2]:
                    count+=1

        return count

