class Solution {
public:
    bool conditionOne(int size, int i, int j, int k)
    {
        if(0 <= i && j > i && k > j && size > k)
        {
            return true;
        }

        return false;
    }

    bool conditionTwo(vector<int>& arr, int i, int j, int k, int a, int b, int c)
    {

        if(abs(arr[i] - arr[j]) <= a && abs(arr[j]  -arr[k]) <= b && abs(arr[i] - arr[k]) <= c)
        {
            return true;
        }

        return false;
    }

    int countGoodTriplets(vector<int>& arr, int a, int b, int c) {

        if(arr.size() < 3)
        {
            return 0;
        }
        int cnt =0;

        int len = arr.size();

        for(int i = 0; i < arr.size(); i++)
        {
            for(int j = i + 1; j < arr.size(); j++)
            {
                for(int k = j + 1; k < arr.size(); k++)
                {
                    if(conditionOne(len, i, j, k) == true && conditionTwo(arr, i, j, k, a, b, c))
					//condition one is not necessary at all
                    {
                        cnt++;
                    }
                }
            }
        }

        return cnt;
    }
};

