        return '-' + s + xs;
    } else { // pos, make max
        for (let i = 0; i < n; i++) { // wanna make max, search from higher position, if larger than next position, insert it will be max
            if (xs > s[i]) {
                return s.slice(0, i) + xs + s.slice(i);
            }
        }
        return s + xs; // not found, add to the end
    }
