class Solution {
    public String maxValue(String n, int x) {
        int sign = 1, start = 0;

        if(n.charAt(0) == '-') {
            sign = -1;
            start = 1;
        }

        for(int i = start; i < n.length(); ++i) {
            int c = n.charAt(i) - 48;

            if(sign * c < sign * x)
                return n.substring(0, i) + x + n.substring(i);
        }

        return n + x;
    }
}
