class Solution {
public:
    string maxValue(string& str, int x) {
        int n = str.size();
        string res;
        char add = x + '0';
        if (str[0] == '-') {
            int idx = 1;
            while (idx < n && str[idx] <= add) idx++;
            res = str.substr(0, idx) + add + str.substr(idx, n-idx);
            return res;
        }
        int idx = 0;
        while (idx < n && str[idx] >= add) idx++;
        res = str.substr(0, idx) + add + str.substr(idx, n-idx);
        return res;
    }
};
