class Solution:
    def maxValue(self, n: str, x: int) -> str:
        ans = []

        neg_num = added = False

        for i in range(len(n)):
            if i == 0 and n[i] == '-':
                neg_num = True
                ans.append('-')
                continue

            if neg_num and not added and int(n[i]) > x:
                ans.append(str(x))
                added = True
            elif not neg_num and not added and int(n[i]) < x:
                ans.append(str(x))
                added = True

            ans.append(n[i])

        if not added:
            ans.append(str(x))

        return ''.join(ans)
