				if (!isInserted && (n[i] - '0') < x)
				{
					res.Append(x);
					isInserted = true;
				}

				res.Append(n[i]);
			}
		}

		if (res.Length == n.Length)
			res.Append(x);

		return res.ToString();
	}
}

