class Solution {
public:
    string maxValue(string n, int x) {
        bool f=0;
        // for negative number we have to insert the given digit prior to the first strictly greater digit in n from left to right
        if(n[0] == '-'){
            for(int i=1;i<n.length();++i)
            {
                if(x<n[i]-'0')
                {
                    n.insert(i,to_string(x));
                    f=1;
                    break;
                }
            }
            if(!f) n+=to_string(x);
        }
        // for positive number we have to insert the digit prior to the first strictly smaller digit in n from left to right 
        else
        {
            for(int i=0;i<n.length();++i){
                if(n[i]-'0'>=x) continue;
                else
                {
                    n.insert(i,to_string(x));
                    f=1;
                    break;
                }
            }
            if(!f) n+=to_string(x);
        }
        return n;
    }
};
