class Solution {
public:
    string maxValue(string n, int x) {
        bool is_neg = (n[0] == '-');
        int sz = n.size();

        if (is_neg) {
            for (int i = 1; i < sz; ++i) {
                int val = n[i] - '0';

                if (val > x) {
                    n.insert(n.begin() + i, (x + '0'));
                    break;
                }
            }
        } else {
            for (int i = 0; i < sz; ++i) {
                int val = n[i] - '0';

                if (val < x) {
                    n.insert(n.begin() + i, (x + '0'));
                    break;
                }
            }
        }

        if (n.size() == sz) {
            n.push_back((x + '0'));
        }

        return n;
    }
};
