impl Solution {
    pub fn max_value(n: String, x: i32) -> String {
      let mut s = n.chars().collect::<Vec<char>>();
      let n = s.len();
      let x = x.to_string().chars().collect::<Vec<char>>()[0];

      if s[0] == '-' {
        for i in 1..n {
          if s[i] > x {
            s.insert(i, x);
            return s.into_iter().collect::<String>();
          }
        }
      } else {
        for i in 0..n {
          if s[i] < x {
            s.insert(i, x);
            return s.into_iter().collect::<String>();
          }
        }
      }
      s.push(x);
      s.into_iter().collect::<String>()
    }
}
