class Solution 
{
public:
    string maxValue(string n, int x) 
    {
        string answer = "";
        if(n[0]!='-')
        {
            // Positive Number
            // Need to Maximize
            //73,6 -> 763
            //13,2 -> 213
            long long int index = -1;
            for(long long int i=0; i<n.length();i++)
            {
                if((n[i]-'0')<x)
                {
                    index = i;
                    break;
                }
            }

            if(index!=-1)
