class Solution {
public:

    int convert(char c) {
        string temp = "";
        temp += c;
        return stoi(temp);
    }

    char charac(int x) {
        string temp = to_string(x);
        return temp[0];
    }

    string maxValue(string s, int x) {
        string ans = "";
        int digit;
        int i = 0, index = -1;
        if(s[0] != '-') {
            while(i < s.length()) {
                digit = convert(s[i]);
                if(x > digit) {
                    index = i;
                    break;
                }
                i++;
            }
        }
        else{
            i = 1;
            while(i < s.length()) {
                digit = convert(s[i]);
                if(x < digit) {
                    index = i;
                    break;
                }
                i++;
            }
        }
        if(index == -1) {
            ans += s;
            ans += charac(x);
            return ans;
        }
        else{
            for(int x=0;x<index;x++) {
                ans += s[x];
            }
            ans += charac(x);
            for(int x=index;x<s.length();x++) {
                ans += s[x];
            }
            return ans;
        }
    }
};
