class Solution {
    // for pozitive:
    // 1. 32938323342, x = 3
    // =>332938323342 (insert when first number to the right is smaller than x)
    // 2. 465, x = 6
    // =>6465
    // 2. 28824579515, x = 8
    // =>828824579515

    // for negative:
    // 1. -293845672, x = 4
    // =>-2493845672 (insert when all the numbers to the left are smaller or equal to x)
    // 2. -132, x = 3
    // =>-1323
    fun maxValue(n: String, x: Int): String {
        if (n[0] == '-') {
            for (i in 1 until n.length) {
                if (n[i].toString().toInt() > x) {
                    var s = StringBuilder(n)
                    s.insert(i, "$x"[0])
                    return s.toString()
                }
            }
            return StringBuilder(n).insert(n.length, "$x"[0]).toString()
        } else {
            for (i in 0 until n.length) {
                if (n[i].toString().toInt() < x) {
                    var s = StringBuilder(n)
                    s.insert(i, "$x"[0])
                    return s.toString()
                }
            }
            return StringBuilder(n).insert(n.length, "$x"[0]).toString()
        }
    }
}
