class Solution {
public:
    string maxValue(string n, int x) {
        const bool sign = (n[0] == '-');
        auto gt = [](int i, int j) {
            return greater<int>()(i, j);
        };
        auto lt = [](int i, int j) {
            return less<int>()(i, j);
        };

        const auto &pred = (sign) ? gt : lt;

        auto it = n.begin();
        if(sign)
            ++it;

        char src = x + '0';
        while(it != n.end()) {
            if(pred(*it, src))
                break;
            ++it;
        }

        n.insert(it, src);
        return n;
    }
};
```

