class Solution {

    public String maxValue(String s, int x) {
        boolean pos=true;
        if(s.charAt(0)=='-'){
            pos=false;
            s=s.substring(1,s.length());
        }

        int n=s.length();

        for(int i=0;i<n;i++){
            if(pos){
                if(s.charAt(i)-'0' < x){
                    //found insertion point.
                   return s.substring(0,i)+x+s.substring(i,s.length()); 
                }
            }else{
                if(s.charAt(i)-'0' > x){
                   return "-"+s.substring(0,i)+x+s.substring(i,s.length());
                }
            }
        }
        return pos==true ? s+x : "-"+s+x; 


    }
}

