class Solution:
    def maxValue(self, n: str, x: int) -> str:
        numDigits, optimalPos = len(n) - 1, len(n)
        if n[0] == '-':
            for i in range(numDigits, 0, -1):
                if ord(n[i]) - ord('0') > x:
                    optimalPos = i
        else:
            for i in range(numDigits, -1, -1):
                if ord(n[i]) - ord('0') < x:
                    optimalPos = i
        return n[:optimalPos] + str(x) + n[optimalPos:]
