    string maxValue(string n, int x) {
        int i =0;
        char a = x + '0'; // store x as character
        if(n[0] != '-'){
            // come here if n > 0
            while(n[i] != '\0'){ // search till n ends
                if(n[i] < a) // if we get character less than a i.e digit less than x put x there and return no .
                    return n.substr(0,i) + a + n.substr(i,n.length()-i);
                i++;
            }
            // program comes here if and only if it didn't found right place so it is in the end 
