class Solution {
public:
    string maxValue(string n, int x) {
        string sx = to_string(x);
        if(n.size() == 0) return sx;
        int dig = 0;
        int len = n.size();
        if(n[0] == '-'){
            dig = 1;
            while(x >= (n[dig]-'0') && dig < len){
                dig++;
            }
        } else {
            dig = 0;
            while(x <= (n[dig]-'0') && dig < len){
                dig++;
            }
        }
        n.insert(dig, sx);
        return n;
    }
};
