class Solution {
public:
    string maxValue(string s, int x) {
        int sz = s.size();
        string res="";
        if(s[0]!='-'){
            for(int i=0; i<sz; ++i){
                if((s[i]-'0') >= x)
                    res.push_back(s[i]);
                else if((s[i]-'0') < x){
                    res.push_back(x+'0');
                    string s1 = s.substr(i, sz-i);
                    res.append(s1);
                    break;
                }
            }
            if(res == s)
                res.push_back(x+'0');
        }
        else if(s[0]=='-'){
            res.push_back('-');
            for(int i=1; i<sz; ++i){
                if((s[i]-'0') <= x)
                    res.push_back(s[i]);
                else if((s[i]-'0') > x){
                    res.push_back(x+'0');
                    string s1 = s.substr(i, sz-i);
                    res.append(s1);
                    break;
                }
            }
            if(res == s)
                res.push_back(x+'0');
        }
        return res;
    }
};
