class Solution {
public:
    string pos(string s,int x)
    {
        int n=s.size(),i=0;
        char c=x+'0';
        for(i=0;i<n;i++)
        {
            int v=s[i]-'0';
            if(x>v) return s.substr(0,i)+c+s.substr(i,n-i+1);
        }
        return s.substr(0,i)+c+s.substr(i,n-i+1);
    }
    string neg(string s,int x){
        int n=s.size(),i=0;
        char c=x+'0';
        for(i=0;i<n;i++)
        {
            int v=s[i]-'0';
            if(x<v) return s.substr(0,i)+c+s.substr(i,n-i+1);
        }

        return s.substr(0,i)+c+s.substr(i,n-i+1);
    }
    string maxValue(string n, int x) {

        if(n[0]=='-')
        {
            return neg(n,x);
        }
        else return pos(n,x);
    }
};
