class Solution {
public:
    string maxValue(string s, int x) {
        if(s[0]=='-')
        {
            int f = 1;
            for(int i = 1;i<s.size();i++)
            {
                if(s[i]-'0'>x)
                {
                    s.insert(s.begin()+i,'0' + x);
                    f = 0;
                    break;
                }
            }
            if(f)s.push_back('0' + x);
            return s;
        }
        else
        {
            int f = 1;
            for(int i = 0;i<s.size();i++)
            {
                if(s[i]-'0'<x)
                {
                    s.insert(s.begin()+i,'0' + x);
                    f = 0;
                    break;
                }
            }
            if(f)s.push_back('0' + x);
            return s;
        }
        return "";
    }
};
