class Solution {

    bool pos_fit(char lhs, char rhs) {
        return lhs < rhs;
    }

    bool neg_fit(char lhs, char rhs) {
        return lhs > rhs;
    }

public:
    string maxValue(string n, int x) {
        char t = x + '0';
        bool neg = n[0] == '-';
        for (int i = 0, len = n.size(); i < len; ++i) {
            if (!neg and pos_fit(n[i], t)) {
                return n.insert(i, 1, t);
            }

            if (neg and neg_fit(n[i], t)) {
                return n.insert(i, 1, t);
            }
        }

        return n.insert(n.size(), 1, t);
    }
};
```

