class Solution:
    def maxValue(self, n: str, x: int) -> str:
        ans = []
        added = False

        if n[0] == '-':
            ans.append('-')

            for i in range(1, len(n)):
                if int(n[i]) > x and not added:
                    ans.append(str(x))
                    added = True
                ans.append(n[i])

            if not added:
                ans.append(str(x))

        else:
            for i in range(len(n)):
                if int(n[i]) < x and not added:
                    ans.append(str(x))
                    added = True
                ans.append(n[i])

            if not added:
                ans.append(str(x))

        return ''.join(ans)
