string maxValue(string n, int x) {

		// -, + => -4213 , 2 => - + 2 + 4213 
       // +. + => 4213 , 2 => 4+ 2 +213

        char ch = to_string(x)[0];

        if(n[0] == '-'){

            for(int i=1; i<n.size(); i++){
                if(ch < n[i]){
                    n.insert(i, to_string(x));
                    return n;
                }
            }
            return n+ch;
        }

        for(int i=0; i<n.size(); i++){

            if(ch > n[i]){
                n.insert(i, to_string(x));
                return n;
            }
        }
        return n+ch;

    }

};

