var lastStoneWeight = function (stones) {
    const heap = new Heap(stones);
    while (heap.size > 1) {
        let x = heap.peek();
        heap.delete();
        let y = heap.peek();
        heap.delete();
        const res = x - y;
        if (res > 0) heap.insert(res);
    }
    if (heap.size) return heap.peek();
    return 0;
}
