class Solution {
public:
    int lastStoneWeight(vector<int>& stones) {
        priority_queue<int> pq;  //create max heap having maximum element at top
        for(int x:stones)               //insert all elements into priority queue 
            pq.push(x);
        while(pq.size()>1){        //iterate until size is equal to 1
            int fval=pq.top();      //fval is first max val
            pq.pop();
            int sval=pq.top();     //sval is second max val
            pq.pop();
            if(fval==sval){          //if both equal simply push 0 since both stones destroyed
                pq.push(0);
            }
            else if(fval>sval){         //if both not equal substract small val to larger  one and insert 
                pq.push(fval-sval);
            }
        }
        return pq.top();       //finally return top value
    }
};
```
