import heapq

class Solution(object):
    def lastStoneWeight(self, stones):
        """
        :type stones: List[int]
        :rtype: int
        """

        heapq.heapify(stones)

        while len(stones) > 1:
            largest = heapq.nlargest(2, stones)
            diff = abs(largest[0] - largest[1])

            for item in largest:
                stones.remove(item)

            if diff > 0:
                heapq.heappush(stones, diff)

        if stones:
            return stones[0]
        return 0

