class Solution {
public:
    int lastStoneWeight(vector<int>& stones) {
        priority_queue<int> pq;
        for(int x: stones){
            pq.push(x);
        }
        int ans = 0;
        while(!pq.empty()){
            int m = pq.top();
            pq.pop();
            if(pq.empty()) ans = m;
            if(!pq.empty()){
                if(m == pq.top()){
                    pq.pop();
                }
                else if(pq.top() < m){
                    m = m - pq.top();
                    pq.pop();
                    pq.push(m);
                }
            }
        }
        return ans;
    }
};
